import Button from "../components/Button/Button";
import {CITIES} from "../const/const";
import {getFormattedDataWeather, getWeather} from "../weather";
import Icon from "../components/Icon/Icon";

const month = ["January", "February", "March", "April", "May", "June", "July",
    "August", "September", "October", "November", "December"];
var days = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday']
const Main = () => {
    const [town, setTown] = useState(CITIES[0]);
    const [weather, setWeather] = useState(null);
    const [timeZone, setTimeZone] = useState(0);
    const [temp, setTemp] = useState(0);
    const [description, setDescription] = useState('');
    const [date, setDate] = useState('')
    const [day, setDay] = useState(0);
    const [daysWeather, setDaysWeather] = useState([]);


    function getLocation() {
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition((position) => {
                // setLat(position.coords.latitude)
                // setLng(position.coords.longitude);
            })
        }
    }



    async function fetchWeather() {
        let res = await getFormattedDataWeather({q: 'Minsk', units: "metric"});
        setWeather(res.formattedCurrentWeather);
    }

    useEffect(() => {
        fetchWeather();


        window.onload = function () {
            getLocation();
        }
    }, [town])

