                <div className={'flex gap-4'}>
                    <Button onClick={() => setTown(CITIES[0])}>
                        Minsk
                    </Button>
                    <Button onClick={() => setTown(CITIES[1])}>
                        Moscow
                    </Button>
                    <Button onClick={() => setTown(CITIES[2])}>
                        Bratislava
                    </Button>
                </div>
                {
                    weather && (
                        <div className={styles.block}>

                            <div className={styles.city}>
                                {weather.name}
                            </div>
                            <div className={styles.info}>
                                <p>{weather.date.day}</p>
                                <p>{weather.date.date}</p>
                            </div>
                            <div className={styles.image}>
                                <div className={'h-24'}>
                                    <Icon className={'fill-white h-full'} name={weather.icon}/>
                                </div>
                                <p>{weather.description}</p>
                            </div>
                            <div className={styles.degree}>
                                {Math.round(weather.temp)}Â°
                            </div>
                        </div>
                    )
                }

                {/*<div className={styles.block}>*/}
                {/*    {*/}
                {/*        daysWeather.map((item, i) => (*/}
                {/*            <DayWeather day={getTime(timeZone, item.dt).day} key={i}/>*/}
                {/*        ))*/}
                {/*    }*/}

                {/*</div>*/}
            </div>
        </div>
    );
};

export default Main;
