import React, {useContext, useEffect, useState} from 'react';
import Button from "../../components/Button/Button";
import Block from "../../components/Block/Block";
import {Context} from "../../context";
import DayWeather from "../../components/DayWeather/DayWeather";
import {CITIES} from "../../const/const";

const Details = () => {
    const { hourlyWeather, fetchWeather } = useContext(Context);
    const [town, setTown] = useState(JSON.parse(localStorage.getItem('town')) || CITIES[0]);

    useEffect(() => {
        if (!town) return;
        fetchWeather(town.lat, town.lon);

        // window.onload = function () {
        //     // getLocation();
        // }
    }, [])

    return (
        <div className={'h-screen flex'}>
            <div className={'container px-4 mx-auto pt-5'}>
                <div className={'mb-5 flex gap-1 items-stretch'}>
                    <input className={'py-2 px-3 rounded-lg flex-auto outline-none'} type={'text'} placeholder={'Enter your city'}/>
                    <Button>Search</Button>
                </div>
                <Block>
                    { hourlyWeather &&
                        hourlyWeather.map(i => (
                            <DayWeather icon={i.icon} day={i.day.time} temp={i.temp}/>
                        ))
                    }
                </Block>
            </div>

        </div>
    );
};

export default Details;
