import React, {useState} from 'react';
import {getFormattedDataWeather} from "../weather";

const initialState = {
    dailyWeather: null,
    hourlyWeather: null,
    weather: null
}

export const Context = React.createContext(initialState);

export const ContextProvider = ({children}) => {
    const [weather, setWeather] = useState(null);
    const [dailyWeather, setDailyWeather] = useState(null);
    const [hourlyWeather, setHourlyWeather] = useState(null);

    async function fetchWeather(lat, lon) {
        let res = await getFormattedDataWeather({lat, lon, units: "metric", exclude: 'minutely'});
        setWeather(res.current);
        setDailyWeather(res.daily.slice(0,3));
        setHourlyWeather(res.hourly);
    }

    return (
        <Context.Provider value={{
            weather,
            setWeather,
            dailyWeather,
            setDailyWeather,
            hourlyWeather,
            setHourlyWeather,
            fetchWeather
        }}>
            {children}
        </Context.Provider>
    )
}
