import React, {useEffect, useState} from 'react';
import styles from './Main.module.scss';
import {Snow} from "../assets/icons";
import DayWeather from "../components/DayWeather/DayWeather";
import Button from "../components/Button/Button";
import {CITIES} from "../const/const";
const month= ["January","February","March","April","May","June","July",
    "August","September","October","November","December"];
var days = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday']
const Main = () => {
    const [town, setTown] = useState(CITIES[0]);
    const [temp, setTemp] = useState(0);
    const [description, setDescription] = useState('');
    const [date, setDate] = useState('')
    const [day, setDay] = useState(0);

    const [lat, setLat] = useState();
    const [lng, setLng] = useState();

    function timeConverter(UNIX_timestamp){
        var a = new Date(UNIX_timestamp * 1000);
        var months = ['Jan','Feb','Mar','Apr','May','Jun','Jul','Aug','Sep','Oct','Nov','Dec'];
        var year = a.getFullYear();
        var month = months[a.getMonth()];
        var date = a.getDate();
        var hour = a.getHours();
        var min = a.getMinutes();
        var sec = a.getSeconds();
        var time = date + ' ' + month + ' ' + year + ' ' + hour + ':' + min + ':' + sec ;
        return time;
    }

    function getLocation() {
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition((position) => {
                setLat(position.coords.latitude)
                setLng(position.coords.longitude);
            })
        }
    }

    function currentTime(timezoneIn, dtIn) {
        let dateTime = new Date(dtIn * 1000 + (timezoneIn * 1000));

        // Convert into 24-hour format
        let hour = (dateTime.getHours() % 12) - 3;
        let ampm = hour >= 12 ? 'pm' : 'am';

        let minutes = dateTime.getMinutes();
        let weekday = dateTime.toLocaleString('default', { weekday: 'long' });
        let month = dateTime.toLocaleString('default', { month: 'short' });
        let date = dateTime;
        setDate(dateTime);

        return `${hour} : ${minutes} ${ampm} - ${weekday} , ${month} ${date}`;
    }

    async function fetchWeather() {


        let res = await fetch(`http://api.openweathermap.org/data/2.5/forecast?lat=${town.lat}&lon=${town.lon}&units=metric&cnt=4&appid=c065f555d4e19864fdc27e420f758663`);
        let w = await res.json();
        const {list} = w;
        const today = list[0];
        setTemp(Math.round(today.main.temp));
        setDescription(today.weather[0].description);
        let zone = w.city.timezone;
        let date = new Date(today.dt*1000-(zone*1000))
        let day = date.getDay();
        setDate(date.toLocaleDateString("en-US"))
        setDay(days[day]);
        console.log('time = ',date)
        console.log('res', w)

    }

    useEffect(() => {
        // if (navigator.geolocation) {
        //     // Передаем две функции
        //     navigator.geolocation.getCurrentPosition((position) => {
        //         setLat(position.coords.longitude);
        //         setLng(position.coords.longitude);
        //     }, geolocationFailure);
        //
        //     // Выводим результат
        //     result.innerHTML = "Поиск начался";
        // }
        // else {
        //     // Выводим результат
        //     result.innerHTML = "Ваш браузер не поддерживает геолокацию";
        // }
    }, [])

    useEffect(()=> {
        fetchWeather();
        window.onload = function() {
            getLocation();
        }
    },[town])

    return (
        <div className={'h-screen flex'}>
            <div className={'container px-4 mx-auto pt-5'}>
                <div className={'flex gap-4'}>
                    <Button onClick={()=>setTown(CITIES[0])}>
                        Minsk
                    </Button>
                    <Button onClick={()=>setTown(CITIES[1])}>
                        Moscow
                    </Button>
                    <Button onClick={()=>setTown(CITIES[2])}>
                        Bratislava
                    </Button>
                </div>
                <div className={styles.block}>
                    <div className={styles.city}>
                        MINSK
                    </div>
                    <div className={styles.info}>
                        <p>{day}</p>
                        <p>{date}</p>
                    </div>
                    <div className={styles.image}>
                        <div>
                            <Snow/>
                        </div>
                        <p>{description}</p>
                    </div>
                    <div className={styles.degree}>
                        {temp}°
                    </div>
                </div>
                <div className={styles.block}>
                    <DayWeather/>
                    <DayWeather/>
                    <DayWeather/>
                </div>
            </div>
        </div>
    );
};

export default Main;
