const API_KEY = 'c065f555d4e19864fdc27e420f758663';
const URL_API = 'https://api.openweathermap.org/data/2.5/';

export function getWeather(type, searchParams) {
    const url = new URL(`${URL_API}/${type}`);
    const params = new URLSearchParams({ ...searchParams, appid: API_KEY });
    url.search = params.toString();

    return fetch(url).then((res) => res.json());
}

function formatCurrentWeather(data) {

    console.log('data ', data)
    const {
        lat,
        lon,
        current: {temp, dt, feels_like},
        // main: { temp, feels_like, temp_min, temp_max, humidity },
        name,
        sys: { country, sunrise, sunset },
        weather,
        wind: { speed },
        timezone
    } = data;

    // console.log('weather[0]', weather[0])
    const { main: details, icon, description } = weather[0];

    return {
        lat,
        lon,
        temp,
        feels_like,
        name,
        dt,
        country,
        sunrise,
        sunset,
        details,
        icon,
        speed,
        description,
        date: getTime(timeToLocal(dt, timezone))
    };
}
export async function getFormattedDataWeather(searchParams) {
    const formattedCurrentWeather = await getWeather(
        "onecall",
        searchParams
    ).then(formatCurrentWeather);
    // getForecastWeather(searchParams)

    // console.log('formatted', formattedCurrentWeather)

    return ({
        formattedCurrentWeather
    })
}

export async function getForecastWeather(searchParams) {
    const dataForecast = await getWeather("onecall", searchParams);

    // console.log('data forecast', dataForecast)
}

export function timeToLocal(sec, timezone) {
    return (new Date(sec*1000+(timezone*1000)).toUTCString())
}

function getTime(date) {
    const localeDate = new Date(date);
    const  day = localeDate.toLocaleDateString("en-GB", {
        weekday: 'long'
    })

    return ({
        day,
        date: localeDate.toLocaleDateString("en-GB")
    })

}
