import React, {useState} from 'react';
import {getFormattedDataWeather} from "../weather";

const initialState = {
    dailyWeather: null,
    hourlyWeather: null,
    weather: null
}

export const Context = React.createContext(initialState);

export const ContextProvider = ({children}) => {
    const [weather, setWeather] = useState(null);
    const [dailyWeather, setDailyWeather] = useState(null);
    const [hourlyWeather, setHourlyWeather] = useState(null);

    async function fetchWeather({lat, lon, q}) {
        let res = null;

        if (q) {
            res = await getFormattedDataWeather('weather',{q, units: "metric"});
        } else {
            res = await getFormattedDataWeather('onecall',{lat, lon, units: "metric", exclude: 'minutely'});
        }

        setWeather(res.current);
        setDailyWeather(res.daily.slice(0,3));
        console.log('dasha' ,res)
        setHourlyWeather(res.hourly);
    }

    return (
        <Context.Provider value={{
            weather,
            setWeather,
            dailyWeather,
            setDailyWeather,
            hourlyWeather,
            setHourlyWeather,
            fetchWeather
        }}>
            {children}
        </Context.Provider>
    )
}
