const API_KEY = 'c065f555d4e19864fdc27e420f758663';
const URL_API = 'https://api.openweathermap.org/data/2.5/';

export function getWeather(type, searchParams) {
    const url = new URL(`${URL_API}/${type}`);
    const params = new URLSearchParams({ ...searchParams, appid: API_KEY });
    url.search = params.toString();



    return fetch(url).then((res) => res.json());
}

function formatForecastWeather(data) {

    console.log('data ', data)
    let {
        lat,
        lon,
        current: {temp, dt, feels_like, weather},
        daily,
        hourly,
        // main: { temp, feels_like, temp_min, temp_max, humidity },
        name,
        // sys: { country, sunrise, sunset },
        // wind: { speed },
        timezone,
        timezone_offset
    } = data;

    daily = daily.slice(1, 7).map((d) => {
        return {
            date: getTime(timeToLocal(d.dt, timezone_offset)),
            temp: d.temp.day,
            icon: d.weather[0].icon,
        };
    });

    hourly = hourly.slice(0, 47).map((h) => {
        return {
            date: getTime(timeToLocal(h.dt, timezone_offset)),
            temp: h.temp,
            icon: h.weather[0].icon,
        };
    });

    console.log('daily', daily)
    const { main: details, icon, description } = weather[0];

    return ({
        current: {
            lat,
            lon,
            temp,
            feels_like,
            city: timezone.split('/')[1],
            dt,
            // country,
            // sunrise,
            // sunset,
            details,
            icon,
            description,
            date: getTime(timeToLocal(dt, timezone_offset))
        },
        daily,
        hourly

    });
}
export async function getFormattedDataWeather(type, searchParams) {
    const formattedCurrentWeather = await getWeather(
        type,
        searchParams
    ).then(formatForecastWeather);
    // getForecastWeather(searchParams)

    console.log('formatted', formattedCurrentWeather)

    return formattedCurrentWeather
}

export async function getForecastWeather(searchParams) {
    const dataForecast = await getWeather("onecall", searchParams);

    // console.log('data forecast', dataForecast)
}

export function timeToLocal(sec, timezone) {
    return (new Date(sec*1000+(timezone*1000)).toUTCString())
}

function getTime(date) {
    const localeDate = new Date(date);
    const  dayLong = localeDate.toLocaleDateString("en-GB", {
        weekday: 'long'
    })

    const  dayShort = localeDate.toLocaleDateString("en-GB", {
        weekday: 'short'
    })

    const time = localeDate.toLocaleTimeString('en-GB').split(':').splice(0, 2).join(':');

    return ({
        dayLong,
        dayShort,
        time,
        date: localeDate.toLocaleDateString("en-GB")
    })

}
