import React, {useEffect, useState} from 'react';
import styles from './Main.module.scss';
import {Snow} from "../assets/icons";
import DayWeather from "../components/DayWeather/DayWeather";
import Button from "../components/Button/Button";

const Main = () => {
    const [town, setTown] = useState('Minsk');
    const [temp, setTemp] = useState(0);

    async function fetchWeather() {


        let res = await fetch(`https://api.openweathermap.org/data/2.5/forecast/weather?q=${town}&units=metric&appid=c065f555d4e19864fdc27e420f758663`);
        let w = await res.json();
        console.log('res', w)

    }

    useEffect(()=> {
        fetchWeather();
    },[])

    return (
        <div className={'h-screen flex'}>
            <div className={'container px-4 mx-auto pt-5'}>
                <div className={'flex gap-4'}>
                    <Button>
                        Минск
                    </Button>
                    <Button>
                        Москва
                    </Button>
                    <Button>
                        Братислава
                    </Button>
                </div>
                <div className={styles.block}>
                    <div className={styles.city}>
                        MINSK
                    </div>
                    <div className={styles.info}>
                        <p>Monday</p>
                        <p>02/03/2001</p>
                    </div>
                    <div className={styles.image}>
                        <div>
                            <Snow/>
                        </div>
                        <p>Partly cloudly</p>
                    </div>
                    <div className={styles.degree}>
                        22°
                    </div>
                </div>
                <div className={styles.block}>
                    <DayWeather/>
                    <DayWeather/>
                    <DayWeather/>
                </div>
            </div>
        </div>
    );
};

export default Main;
