import React, {useState} from 'react';
import {getDataCurrentWeather, getFormattedDataWeather} from "../weather";

const initialState = {
    dailyWeather: null,
    hourlyWeather: null,
    weather: null,
    fetchWeather: () => undefined,
    fetchCurrentWeather: () => undefined,
    loader: false

}

export const Context = React.createContext(initialState);

export const ContextProvider = ({children}) => {
    const [weather, setWeather] = useState(null);
    const [dailyWeather, setDailyWeather] = useState(null);
    const [hourlyWeather, setHourlyWeather] = useState(null);
    const [loader, setLoader] = useState(false);

    async function fetchWeather({lat, lon}) {
        try {
            let res = await getFormattedDataWeather({lat, lon, units: "metric", exclude: 'minutely'});

            setWeather(res.current);
            setDailyWeather(res.daily.slice(0,3));
            setHourlyWeather(res.hourly);

            const {city, lat: latData, lon: lonData} = res.current;
            localStorage.setItem('town', JSON.stringify({town: city, lat: latData, lon: lonData}));
        } catch(err) {
            console.log(err)
        } finally {
            setLoader(false);
        }
    }

    async function fetchCurrentWeather({q}) {
        let res = await getDataCurrentWeather({q, units: "metric"});
        setWeather(res.current);
        setDailyWeather(res.daily.slice(0,3));
        setHourlyWeather(res.hourly);
        const {city, lat, lon} = res.current;
        localStorage.setItem('town', JSON.stringify({town: city, lat, lon}));
    }

    return (
        <Context.Provider value={{
            weather,
            setWeather,
            dailyWeather,
            setDailyWeather,
            hourlyWeather,
            setHourlyWeather,
            fetchWeather,
            fetchCurrentWeather
        }}>
            {children}
        </Context.Provider>
    )
}
