import React, {useContext, useEffect, useState} from 'react';
import styles from './Main.module.scss';
import {Snow} from "../assets/icons";
import DayWeather from "../components/DayWeather/DayWeather";
import Button from "../components/Button/Button";
import {CITIES} from "../const/const";
import {getFormattedDataWeather, getWeather} from "../weather";
import Icon from "../components/Icon/Icon";
import {Link} from "react-router-dom";
import {Context} from "../context";

const month = ["January", "February", "March", "April", "May", "June", "July",
    "August", "September", "October", "November", "December"];
var days = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday']
const Main = () => {
    const { weather, fetchWeather} = useContext(Context);
    const [town, setTown] = useState(JSON.parse(localStorage.getItem('town')) || CITIES[0]);

    function getLocation() {
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition((position) => {
                if (!localStorage.getItem('town')) {
                    setTown({lat: position.coords.latitude, lon: position.coords.longitude})
                }
            })
        }
    }

    useEffect(() => {
        if (!town) return;

        localStorage.setItem('town', JSON.stringify(town));
    }, [town])


    useEffect(() => {
        if (!town) return;
        fetchWeather();


        window.onload = function () {
            getLocation();
        }
    }, [town])

    return (
        <div className={'h-screen flex'}>
            <div className={'container px-4 mx-auto pt-5'}>
                <div className={'flex gap-1 sm:gap-4'}>
                    <Button className={`${town.name === 'Minsk' ? 'active' : ''}`} onClick={() => setTown(CITIES[0])}>
                        Minsk
                    </Button>
                    <Button className={`${town.name === 'Moscow' ? 'active' : ''}`} onClick={() => setTown(CITIES[1])}>
                        Moscow
                    </Button>
                    <Button className={`${town.name === 'Bratislava' ? 'active' : ''}`} onClick={() => setTown(CITIES[2])}>
                        Bratislava
                    </Button>
                    <Link to={'/details'} className={'text-white underline ml-auto py-2 px-3'}>Details</Link>
                </div>
                {
                    weather && (
                        <div className={styles.block}>

                            <div className={styles.city}>
                                {weather.city}
                            </div>
                            <div className={styles.info}>
                                <p>{weather.date.dayLong}</p>
                                <p>{weather.date.date}</p>
                            </div>
                            <div className={styles.image}>
                                <div className={'h-24 sm:mb-5'}>
                                    <Icon className={'fill-white h-full'} name={weather.icon}/>
                                </div>
                                <p>{weather.description}</p>
                            </div>
                            <div className={styles.degree}>
                                {Math.round(weather.temp)}°
                            </div>
                        </div>
                    )
                }

                {dailyWeather && (<div className={styles.block}>
                    {
                        dailyWeather.map((item, i) => (
                            <DayWeather day={item.date.dayShort} className={'w-1/4'} temp={item.temp} icon={item.icon} key={i}/>
                        ))
                    }

                </div>)}
            </div>
        </div>
    );
};

export default Main;
